import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';

const ViewDetails = () => {
  const { studentid } = useParams(); // URL-dən gələn ID
  const [studentData, setStudentData] = useState(null); // Başlanğıc olaraq null

  useEffect(() => {
    console.log("Fetching data for student ID:", studentid); // Debug üçün konsola yazdırırıq

    fetch("http://localhost:3400/students") // Bütün tələbələri gətiririk
      .then((res) => {
        if (!res.ok) {
          throw new Error("Server error: " + res.status); // Xəta varsa, onu qeyd edirik
        }
        return res.json();
      })
      .then((data) => {
        console.log("Fetched data:", data); // Gətirilən məlumatı yoxlayırıq
        const student = data.students.find(
          (s) => s.id === parseInt(studentid)
        ); // Düzgün ID ilə tələbəni tapırıq
        console.log("Found student:", student); // Tapılan tələbəni konsola yazdırırıq
        setStudentData(student || null); // Tələbə yoxdursa null
      })
      .catch((err) => console.error("Fetch error:", err.message)); // Xətanı qeyd edirik
  }, [studentid]);

  return (
    <div className="container">
      <h2>Tələbə Məlumatları</h2>
      {studentData ? (
        <div className="details">
          <p><strong>ID: </strong>{studentData.id}</p>
          <p><strong>Adı: </strong>{studentData.name}</p>
          <p><strong>Ünvanı: </strong>{studentData.place}</p>
          <p><strong>Mobil nömrəsi: </strong>{studentData.phone}</p>
        </div>
      ) : (
        <p>Məlumat tapılmadı və ya yüklənir...</p>
      )}
      <Link to="/" className="btn btn-back">Back</Link>
    </div>
  );
};

export default ViewDetails;
