import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';

const ViewDetails = () => {
  const { studentid } = useParams(); // Düzəliş
  const [studentData, setStudentData] = useState({});

  useEffect(() => {
    fetch(`http://localhost:3400/students`)
  .then((res) => res.json())
  .then((data) => {
    const student = data.students.find((s) => s.id === parseInt(studentid));
    setStudentData(student);
  })
  .catch((err) => console.log("Xəta:", err.message));

  }, []); // `studentid` dependency olaraq əlavə olundu

  return (
    <div className="container">
      <h2>Tələbə Məlumatları</h2>
      {studentData && (
        <div className="details">
          <p><strong>ID: </strong>{studentData.id}</p>
          <p><strong>Adı: </strong>{studentData.name}</p>
          <p><strong>Ünvanı: </strong>{studentData.place}</p>
          <p><strong>Mobil nömrəsi: </strong>{studentData.phone}</p>
        </div>
      )}
      <Link to="/" className="btn btn-back">Back</Link>
    </div>
  );
};

export default ViewDetails;
