import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';

const ViewDetails = () => {
  const { studentid } = useParams(); // Parametrdən ID alırıq
  const [studentData, setStudentData] = useState(null); // Məlumat yoxdur deyə null təyin edirik

  useEffect(() => {
    fetch("http://localhost:3400/students") // Bütün tələbələri gətiririk
      .then((res) => res.json())
      .then((data) => {
        const student = data.students.find((s) => s.id === parseInt(studentid)); // Daxilində `id`-yə uyğun tələbəni tapırıq
        setStudentData(student || null); // Tapılmırsa null qaytar
      })
      .catch((err) => console.log("Xəta:", err.message));
  }, [studentid]); // studentid dəyişəndə yenidən sorğu

  return (
    <div className="container">
      <h2>Tələbə Məlumatları</h2>
      {studentData ? (
        <div className="details">
          <p><strong>ID: </strong>{studentData.id}</p>
          <p><strong>Adı: </strong>{studentData.name}</p>
          <p><strong>Ünvanı: </strong>{studentData.place}</p>
          <p><strong>Mobil nömrəsi: </strong>{studentData.phone}</p>
        </div>
      ) : (
        <p>Tələbə tapılmadı və ya məlumat yüklənir...</p>
      )}
      <Link to="/" className="btn btn-back">Back</Link>
    </div>
  );
};

export default ViewDetails;
